import React, { useState } from 'react';
import './App.css';

function App() {
  const [items, setItems] = useState([]);
  const [itemName, setItemName] = useState('');
  const [itemPrice, setItemPrice] = useState('');
  const [editingIndex, setEditingIndex] = useState(-1);

  const handleAddItem = () => {
    if (itemName.trim() === '' || itemPrice.trim() === '') return;

    const newItem = { name: itemName, price: itemPrice };

    if (editingIndex === -1) {
      setItems([...items, newItem]);
    } else {
      const updatedItems = items.map((item, index) => 
        index === editingIndex ? newItem : item
      );
      setItems(updatedItems);
      setEditingIndex(-1);
    }

    setItemName('');
    setItemPrice('');
  };

  const handleEditItem = (index) => {
    setItemName(items[index].name);
    setItemPrice(items[index].price);
    setEditingIndex(index);
  };

  const handleDeleteItem = (index) => {
    setItems(items.filter((_, i) => i !== index));
  };

  return (
    <div className="App">
      <h1>CRUD Barang</h1>

      <div className="form">
        <input
          type="text"
          value={itemName}
          onChange={(e) => setItemName(e.target.value)}
          placeholder="Nama Barang"
        />
        <input
          type="number"
          value={itemPrice}
          onChange={(e) => setItemPrice(e.target.value)}
          placeholder="Harga Barang"
        />
        <button onClick={handleAddItem}>
          {editingIndex === -1 ? 'Tambah Barang' : 'Update Barang'}
        </button>
      </div>

      {items.length > 0 ? (
        <table className="items-table">
          <thead>
            <tr>
              <th>No</th>
              <th>Nama Barang</th>
              <th>Harga</th>
              <th>Aksi</th>
            </tr>
          </thead>
          <tbody>
            {items.map((item, index) => (
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{item.name}</td>
                <td>{item.price}</td>
                <td>
                  <button className="edit-btn" onClick={() => handleEditItem(index)}>Edit</button>
                  <button className="delete-btn" onClick={() => handleDeleteItem(index)}>Hapus</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>Belum ada barang.</p>
      )}
    </div>
  );
}

export default App;
