import React, { useState } from 'react';
import './App.css';

function App() {
  // State untuk menyimpan barang-barang
  const [items, setItems] = useState([]);
  const [itemName, setItemName] = useState('');
  const [editingIndex, setEditingIndex] = useState(-1);

  // Handle penambahan barang baru
  const handleAddItem = () => {
    if (itemName.trim() === '') return;
    if (editingIndex === -1) {
      setItems([...items, itemName]);
    } else {
      // Update barang yang sedang di-edit
      const updatedItems = items.map((item, index) => 
        index === editingIndex ? itemName : item
      );
      setItems(updatedItems);
      setEditingIndex(-1);
    }
    setItemName('');
  };

  // Handle pengeditan barang
  const handleEditItem = (index) => {
    setItemName(items[index]);
    setEditingIndex(index);
  };

  // Handle penghapusan barang
  const handleDeleteItem = (index) => {
    setItems(items.filter((_, i) => i !== index));
  };

  return (
    <div className="App">
      <h1>CRUD Barang</h1>

      <div>
        <input
          type="text"
          value={itemName}
          onChange={(e) => setItemName(e.target.value)}
          placeholder="Nama barang"
        />
        <button onClick={handleAddItem}>
          {editingIndex === -1 ? 'Tambah Barang' : 'Update Barang'}
        </button>
      </div>

      <ul>
        {items.map((item, index) => (
          <li key={index}>
            {item}
            <button onClick={() => handleEditItem(index)}>Edit</button>
            <button onClick={() => handleDeleteItem(index)}>Hapus</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
